document.addEventListener("scroll",e=>{let t=document.querySelector(".header");window.scrollY>0?t.classList.add("bg"):t.classList.remove("bg")});let menuToggle=document.querySelector(".menu-toggle");menuToggle.addEventListener("click",e=>{let t=document.querySelector(".menu");t.classList.contains("toggle")?t.classList.remove("toggle"):t.classList.add("toggle")});let contactForm=document.querySelector("#contact-form");contactForm.addEventListener("submit",e=>{e.preventDefault(),window.fetch("/email/send",{method:"POST",headers:{"Content-Type":"application/json; charset=UTF-8"},body:JSON.stringify({name:e.target.name.value,email:e.target.email.value,subject:e.target.subject.value,message:e.target.message.value})}).then(e=>e.json()).then(e=>{console.log(e);let t=document.querySelector("#alert");t.innerHTML=e.message,t.style=`color: ${e.isSend?"#4CAF50":"#F44336"}`})});