document.addEventListener("scroll",e=>{let t=document.querySelector(".header");window.scrollY>0?t.classList.add("bg"):t.classList.remove("bg")});let menuToggle=document.querySelector(".menu-toggle");menuToggle.addEventListener("click",e=>{let t=document.querySelector(".menu");t.classList.contains("toggle")?t.classList.remove("toggle"):t.classList.add("toggle")});